#include <bits/stdc++.h>
using namespace std;
int longestArithSeqLength(vector<int> &nums)
{
    unordered_map<int, unordered_map<int, int>> dp;
    int res = 2, n = nums.size();
    if (n == 2)
        return 2;
    for (int i = 0; i < n; ++i)
    {
        for (int j = i + 1; j < n; ++j)
        {
            int d = nums[j] - nums[i];
            dp[d][j] = dp[d].count(i) ? dp[d][i] + 1 : 2;
            res = max(res, dp[d][j]);
        }
    }
    return res;
}

int longestArithSeqLength_brute(vector<int> &nums) // TLE
{

    int maxcount = 2;
    int n = nums.size();
    if (n == 2)
        return 2;
    for (int i = 0; i < n; i++)
    {
        for (int j = i + 1; j < n; j++)
        {
            int off = 2;
            int diff = nums[j] - nums[i];
            for (int k = j + 1; k < n; k++)
            {
                if (nums[k] == nums[i] + (diff * off))
                {
                    off++;
                }
            }
            if (off > maxcount)
                maxcount = off;
        }
    }
    return maxcount;
}
